      ...insertUser, 
      id,
      planType: insertUser.planType || 'standard',
      status: insertUser.status || 'active',
      isAdmin: insertUser.isAdmin || false,
      createdAt: new Date(),
      jellyfinUserId: null,
      stripeCustomerId: null,
      stripeSubscriptionId: null,
      expiresAt: null
