  async createContactMessage(insertMessage: InsertContactMessage): Promise<ContactMessage> {
    const id = randomUUID();
    const message: ContactMessage = {
      ...insertMessage,
      id,
      phone: insertMessage.phone || null,
      status: 'new',
      createdAt: new Date()
    };
    this.contactMessages.set(id, message);
    return message;
  }

