  }

  body {
    @apply font-sans antialiased;
    background: hsl(240, 10%, 6%) !important;
    color: hsl(0, 0%, 98%) !important;
  }
}

@layer components {
  .hero-gradient {
    background: linear-gradient(135deg, hsl(240, 10%, 6%) 0%, hsl(240, 10%, 10%) 50%, hsl(240, 10%, 6%) 100%);
  }

  .luxury-card {
    background: linear-gradient(145deg, rgba(39, 39, 42, 0.95) 0%, rgba(24, 24, 27, 0.95) 100%) !important;
    border: 1px solid rgba(161, 161, 170, 0.2) !important;
    backdrop-filter: blur(10px);
    color: hsl(0, 0%, 98%) !important;
  }

  .media-card {
    @apply transition-all duration-300 ease-out;
  }

  .media-card:hover .media-overlay {
    @apply opacity-100;
  }

  .media-overlay {
    @apply opacity-0 transition-all duration-300;
  }

  .alfredflix-gradient {
    background: linear-gradient(90deg, hsl(var(--alfredflix-primary)), hsl(var(--alfredflix-primary-dark)));
  }

  .alfredflix-text-gradient {
    background: linear-gradient(90deg, hsl(var(--alfredflix-primary)), hsl(var(--alfredflix-primary-dark)));
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
  }

  /* Global text improvements */
  .text-zinc-400 {
    color: rgb(161 161 170) !important;
  }

  .text-zinc-300 {
    color: rgb(212 212 216) !important;
  }

  .text-zinc-200 {
    color: rgb(228 228 231) !important;
  }

  .text-white {
    color: rgb(255 255 255) !important;
  }

  /* Button text visibility */
  .button, button {
    color: inherit !important;
  }

  /* Card content visibility */
  .luxury-card p, .luxury-card span, .luxury-card div {
    color: rgb(228 228 231) !important;
  }

  /* Navigation text */
  nav a, nav button, nav span {
    color: inherit !important;
  }

  /* Stripe Elements Styling */
  .payment-element-container {
    @apply bg-zinc-800/30 rounded-lg p-6 border border-zinc-700/30;
  }

  .StripeElement {
    @apply bg-zinc-800/50 border border-zinc-700/50 rounded-md p-4 transition-colors;
  }

  /* Enhanced media card styling with consistent 3:2 aspect ratio */
  .media-card {
    @apply transition-all duration-300 ease-out;
  }

  .media-card img {
    object-fit: cover;
    object-position: center;
  }

  /* Ensure consistent aspect ratio across all card sizes */
  .media-card .aspect-\[3\/2\] {
    aspect-ratio: 3/2;
  }

  /* Hide scrollbars but keep functionality */
  .scrollbar-hide {
    -ms-overflow-style: none;  /* IE and Edge */
    scrollbar-width: none;     /* Firefox */
  }

  .scrollbar-hide::-webkit-scrollbar {
    display: none;             /* Chrome, Safari, Opera */
  }

  /* Mobile text truncation utility */
  .line-clamp-3 {
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
    overflow: hidden;
