import { useEffect, useRef, useState } from 'react';
import { ArrowLeft, Settings, BarChart3, X } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { jellyfinApi, type JellyfinItem } from '@/lib/jellyfin-api';
import { usePlaybackReport } from '@/hooks/use-jellyfin';

interface VideoPlayerProps {
  item: JellyfinItem | null;
  open: boolean;
  onClose: () => void;
  quality?: 'auto' | '1080p' | '4k';
}

interface PlaybackStats {
  quality: string;
  bitrate: number;
  audioCodec: string;
  videoCodec: string;
  directStream: boolean;
  droppedFrames: number;
}

export function VideoPlayer({ item, open, onClose, quality = 'auto' }: VideoPlayerProps) {
  const videoRef = useRef<HTMLVideoElement>(null);
  const [showStats, setShowStats] = useState(false);
  const [playbackStats, setPlaybackStats] = useState<PlaybackStats>({
    quality: 'Auto',
    bitrate: 0,
    audioCodec: 'AAC',
    videoCodec: 'H.264',
    directStream: true,
    droppedFrames: 0
  });
  const [prerollPlayed, setPrerollPlayed] = useState(false);
  const [showControls, setShowControls] = useState(true);
  const [controlsTimeout, setControlsTimeout] = useState<NodeJS.Timeout>();
  const playbackReport = usePlaybackReport();

  // Auto-hide controls after 3 seconds of inactivity
  useEffect(() => {
    const resetControlsTimeout = () => {
      if (controlsTimeout) clearTimeout(controlsTimeout);
      setShowControls(true);
      const timeout = setTimeout(() => setShowControls(false), 3000);
      setControlsTimeout(timeout);
    };

    const handleMouseMove = () => resetControlsTimeout();
    const handleClick = () => resetControlsTimeout();

    if (open) {
      resetControlsTimeout();
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('click', handleClick);
    }

    return () => {
      if (controlsTimeout) clearTimeout(controlsTimeout);
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('click', handleClick);
    };
  }, [open]);

  // Update playback stats every second
  useEffect(() => {
    if (!open || !item) return;

    const statsInterval = setInterval(() => {
      if (videoRef.current && !videoRef.current.paused) {
        setPlaybackStats(prev => ({
          ...prev,
          quality: videoRef.current?.videoHeight ? `${videoRef.current.videoHeight}p HD` : 'Auto',
          bitrate: Math.floor(Math.random() * 10 + 15), // Simulate dynamic bitrate 15-25
          audioCodec: 'AAC 5.1',
          videoCodec: videoRef.current?.videoHeight && videoRef.current?.videoHeight >= 2160 ? 'H.265' : 'H.264',
          droppedFrames: Math.floor(Math.random() * 3)
        }));
      }
    }, 1000);

    return () => clearInterval(statsInterval);
  }, [open, item]);

  useEffect(() => {
    if (open && item && videoRef.current) {
      initializePlayer();
    }
  }, [open, item]);

  const initializePlayer = async () => {
    if (!item || !videoRef.current) return;

    try {
      // Start with pre-roll video
      if (!prerollPlayed) {
        // In a real implementation, you would load the pre-roll video first
        // videoRef.current.src = '/alfredflix.mp4';
        // videoRef.current.play();

        // For now, skip directly to main content
        loadMainContent();
      }
    } catch (error) {
      console.error('Failed to initialize player:', error);
    }
  };

  const loadMainContent = () => {
    if (!item || !videoRef.current) return;

    const streamUrl = jellyfinApi.getStreamUrl(item.Id, quality);
    videoRef.current.src = streamUrl;
    videoRef.current.play();

    // Report playback start
    playbackReport.mutate({
      itemId: item.Id,
      action: 'start'
    });

    setPrerollPlayed(true);
  };

  const handleTimeUpdate = () => {
    if (!item || !videoRef.current) return;

    const currentTime = videoRef.current.currentTime;
    const duration = videoRef.current.duration;
    const positionTicks = Math.floor(currentTime * 10000000); // Convert to ticks

    // Report progress every 10 seconds
    if (Math.floor(currentTime) % 10 === 0) {
      playbackReport.mutate({
        itemId: item.Id,
        action: 'progress',
        positionTicks,
        isPaused: videoRef.current.paused
      });
    }
  };

  const handleClose = () => {
    if (videoRef.current) {
      videoRef.current.pause();
      videoRef.current.src = '';
    }
    setPrerollPlayed(false);
    onClose();
  };

  const toggleStats = () => {
    setShowStats(!showStats);
  };

  if (!open || !item) return null;

  return (
    <div className="fixed inset-0 z-50 bg-black" data-testid="video-player">
      <div className="w-full h-full relative">
        {/* Top Controls Bar - Only show when controls are visible */}
        {showControls && (
          <div className="absolute top-4 left-4 right-4 z-20 flex justify-between items-center bg-black/30 backdrop-blur-sm rounded-lg px-4 py-2">
            <Button
              variant="ghost"
              size="sm"
              onClick={handleClose}
              className="text-white hover:bg-white/20"
              data-testid="back-button"
            >
              <ArrowLeft className="w-5 h-5 mr-2" />
              Back
            </Button>

            <div className="flex items-center space-x-2">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setShowStats(!showStats)}
                className="text-white hover:bg-white/20"
                data-testid="stats-toggle"
              >
                <BarChart3 className="w-4 h-4 mr-2" />
                Stats
              </Button>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => {}}
                className="text-white hover:bg-white/20"
                data-testid="settings-button"
              >
                <Settings className="w-4 h-4 mr-2" />
                Quality
              </Button>
            </div>
          </div>
        )}

        {/* Video Container */}
        <div className="w-full h-full">
          <video
            ref={videoRef}
            className="w-full h-full object-contain"
            controls={false}
            onTimeUpdate={handleTimeUpdate}
            data-testid="video-element"
          />
        </div>

        {/* Custom Controls Overlay - Only show when controls are visible */}
        {showControls && (
          <div className="absolute bottom-4 left-4 right-4 z-20 bg-black/30 backdrop-blur-sm rounded-lg px-4 py-3">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <div>
                  <h2 className="text-lg font-semibold text-white" data-testid="player-title">
                    {item.Name}
                  </h2>
                  <p className="text-sm text-zinc-300" data-testid="player-subtitle">
                    {playbackStats.quality} â€¢ {playbackStats.bitrate} Mbps â€¢ {playbackStats.audioCodec}
                  </p>
                </div>
              </div>

              <div className="flex items-center space-x-2">
                <span className="text-xs text-zinc-400">
                  {playbackStats.directStream ? 'Direct Stream' : 'Transcoding'}
                </span>
              </div>
            </div>
          </div>
        )}

        {/* Stats Overlay */}
        {showStats && (
          <Card className="absolute top-20 right-4 bg-black/90 border-zinc-700 backdrop-blur-sm" data-testid="stats-overlay">
            <CardContent className="p-4 space-y-3">
              <div className="flex items-center justify-between mb-2">
                <h3 className="font-semibold text-white">Playback Stats</h3>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setShowStats(false)}
                  className="text-white hover:text-amber-500"
                  data-testid="close-stats"
                >
                  <X className="w-4 h-4" />
                </Button>
              </div>
              <div className="space-y-3 text-sm">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <span className="text-zinc-400">Quality:</span>
                    <div className="text-white font-medium">{playbackStats.quality}</div>
                  </div>
                  <div>
                    <span className="text-zinc-400">Bitrate:</span>
                    <div className="text-white font-medium">{playbackStats.bitrate} Mbps</div>
                  </div>
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <span className="text-zinc-400">Video:</span>
                    <div className="text-white font-medium">{playbackStats.videoCodec}</div>
                  </div>
                  <div>
                    <span className="text-zinc-400">Audio:</span>
                    <div className="text-white font-medium">{playbackStats.audioCodec}</div>
                  </div>
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <span className="text-zinc-400">Stream Type:</span>
                    <div className={`font-medium ${playbackStats.directStream ? "text-green-400" : "text-orange-400"}`}>
                      {playbackStats.directStream ? "Direct" : "Transcode"}
                    </div>
                  </div>
                  <div>
                    <span className="text-zinc-400">Dropped:</span>
                    <div className="text-white font-medium">{playbackStats.droppedFrames} frames</div>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

client/src/hooks/use-auth.tsx.content:
import { createContext, useContext, useEffect, useState } from 'react';
import { jellyfinApi, type JellyfinUser } from '@/lib/jellyfin-api';

interface AuthUser extends JellyfinUser {
  planType: 'standard' | 'premium';
  accessToken: string;
  isAdmin?: boolean;
}

interface AuthContextType {
  user: AuthUser | null;
  isLoading: boolean;
  login: (username: string, password: string) => Promise<void>;
  logout: () => void;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: React.ReactNode }) {
  const [user, setUser] = useState<AuthUser | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Check for existing session
    const storedUser = localStorage.getItem('alfredflix_user');
    const storedToken = localStorage.getItem('alfredflix_token');

    if (storedUser && storedToken) {
      try {
        const userData = JSON.parse(storedUser);
        const authUser = { ...userData, accessToken: storedToken };
        setUser(authUser);

        // Set the API token for Jellyfin requests
        jellyfinApi.setCredentials(storedToken, userData.Id);
      } catch (error) {
        console.error('Failed to restore user session:', error);
        localStorage.removeItem('alfredflix_user');
        localStorage.removeItem('alfredflix_token');
      }
    }

    setIsLoading(false);
  }, []);

  const login = async (username: string, password: string) => {
    try {
      setIsLoading(true);
      const authResult = await jellyfinApi.authenticate(username, password);

      // Determine plan type based on Jellyfin user policy
      const planType = determinePlanType(authResult.User);

      const authUser: AuthUser = {
        ...authResult.User,
        planType,
        accessToken: authResult.AccessToken,
        isAdmin: authResult.User.Policy?.IsAdministrator || false
      };

      setUser(authUser);

      // Store in localStorage for session persistence
      localStorage.setItem('alfredflix_user', JSON.stringify(authUser));
      localStorage.setItem('alfredflix_token', authResult.AccessToken);
    } catch (error) {
      throw error;
    } finally {
      setIsLoading(false);
    }
  };

  const logout = () => {
    setUser(null);
    jellyfinApi.logout();
    localStorage.removeItem('alfredflix_user');
    localStorage.removeItem('alfredflix_token');
  };

  const determinePlanType = (user: JellyfinUser): 'standard' | 'premium' => {
    // All admins get Premium automatically
    if (user.Policy?.IsAdministrator) {
      return 'premium';
    }

    // Check if user has access to UHD libraries or more than 2 streams
    const maxStreams = user.Policy?.MaxActiveSessions || 1;
    const enabledFolders = user.Policy?.EnabledFolders || [];

    // If user has 4+ streams or access to UHD content, they're premium
    if (maxStreams >= 4 || enabledFolders.length > 2) {
      return 'premium';
    }

    return 'standard';
  };

  return (
    <AuthContext.Provider value={{ user, isLoading, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

client/src/hooks/use-jellyfin.tsx.content:
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { jellyfinApi, type JellyfinItem } from '@/lib/jellyfin-api';
import { useAuth } from './use-auth';

export function useJellyfinLibraries() {
  const { user } = useAuth();

  return useQuery({
    queryKey: ['jellyfin', 'libraries'],
    queryFn: () => jellyfinApi.getLibraries(),
    enabled: !!user,
    staleTime: 5 * 60 * 1000, // 5 minutes
  });
}

export function useContinueWatching() {
  const { user } = useAuth();

  return useQuery({
    queryKey: ['jellyfin', 'continue-watching'],
    queryFn: () => jellyfinApi.getContinueWatching(),
    enabled: !!user,
    refetchInterval: 30000, // Refetch every 30 seconds
  });
}

export function useNextUp() {
  const { user } = useAuth();

  return useQuery({
    queryKey: ['jellyfin', 'next-up'],
    queryFn: () => jellyfinApi.getNextUp(),
    enabled: !!user,
    refetchInterval: 30000,
  });
}

export function useLatestItems(parentId?: string) {
  const { user } = useAuth();

  return useQuery({
    queryKey: ['jellyfin', 'latest', parentId],
    queryFn: () => jellyfinApi.getLatestItems(parentId),
    enabled: !!user,
    staleTime: 5 * 60 * 1000,
  });
}

export function useJellyfinSearch(query: string) {
  const { user } = useAuth();

  return useQuery({
    queryKey: ['jellyfin', 'search', query],
    queryFn: () => jellyfinApi.searchItems(query),
    enabled: !!user && query.length > 2,
    staleTime: 30000,
  });
}

export function useJellyfinItem(itemId: string) {
  const { user } = useAuth();

  return useQuery({
    queryKey: ['jellyfin', 'item', itemId],
    queryFn: () => jellyfinApi.getItem(itemId),
    enabled: !!user && !!itemId,
  });
}

export function usePlaybackReport() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async ({ itemId, action, positionTicks, isPaused }: {
      itemId: string;
      action: 'start' | 'progress' | 'stop';
      positionTicks?: number;
      isPaused?: boolean;
    }) => {
      if (action === 'start') {
        await jellyfinApi.reportPlaybackStart(itemId);
      } else if (action === 'progress' && positionTicks !== undefined && isPaused !== undefined) {
        await jellyfinApi.reportPlaybackProgress(itemId, positionTicks, isPaused);
      }
    },
    onSuccess: () => {
      // Invalidate continue watching and next up queries to reflect updated progress
      queryClient.invalidateQueries({ queryKey: ['jellyfin', 'continue-watching'] });
      queryClient.invalidateQueries({ queryKey: ['jellyfin', 'next-up'] });
    }
  });
}

